mapper.select:
SELECT f_id,f_name, f_advanced_params,f_description,f_table_rows,f_data_source_id,f_operation_time FROM
(
SELECT f_id,f_name, f_advanced_params,f_description,f_table_rows,f_data_source_id,${sortOrder} as
sort_order,f_operation_time FROM t_table_scan
WHERE 1=1

    AND f_data_source_id IN

        #{dsId}


AND f_operation_type != 1

    AND f_operation_time >= #{updateTime}


    AND f_name LIKE CONCAT('%', #{keyword}, '%')

UNION ALL
SELECT f_id,f_name,f_advanced_params,f_description,f_table_rows,f_data_source_id, ${sortOrder} as
sort_order,f_update_time as f_operation_time FROM t_table
WHERE 1=1

    AND f_data_source_id IN

        #{dsId}


AND f_delete_flag != 1

    AND f_update_time >= #{updateTime}


    AND f_name LIKE CONCAT('%', #{keyword}, '%')

)t
ORDER BY t.sort_order ${direction}

    LIMIT #{limit} OFFSET #{offset}

SELECT * FROM t_table_scan
    WHERE f_data_source_id = #{dsId} AND f_operation_type != 1

    AND f_name LIKE CONCAT('%', #{keyword}, '%')

ORDER BY ${sortOrder} ${direction}

    LIMIT #{limit} OFFSET #{offset}

SELECT * FROM t_table_scan
WHERE 1=1

    AND f_id IN

        #{includeId}


AND f_operation_type != 1

    AND f_name LIKE CONCAT('%', #{keyword}, '%')

ORDER BY ${sortOrder} ${direction}

    LIMIT #{limit} OFFSET #{offset}

SELECT f_id,f_name, f_advanced_params,f_description,f_table_rows,f_data_source_id,f_operation_time FROM ( SELECT f_id,f_name, f_advanced_params,f_description,f_table_rows,f_data_source_id,${sortOrder} as sort_order,f_operation_time FROM t_table_scan WHERE 1=1
AND f_id IN
#{includeId}
AND f_operation_type != 1
AND f_name LIKE CONCAT('%', #{keyword}, '%')
UNION ALL SELECT f_id,f_name,f_advanced_params,f_description,f_table_rows,f_data_source_id, ${sortOrder} as sort_order,f_update_time as f_operation_time FROM t_table WHERE 1=1
AND f_id IN
#{includeId}
AND f_delete_flag != 1
AND f_name LIKE CONCAT('%', #{keyword}, '%')
) t ORDER BY t.sort_order ${direction}
LIMIT #{limit} OFFSET #{offset}

SELECT count(*) FROM ( SELECT f_id FROM t_table_scan WHERE 1=1
AND f_data_source_id IN
#{dsId}
AND f_operation_type != 1
AND f_operation_time >= #{updateTime}
AND f_name LIKE CONCAT('%', #{keyword}, '%')
UNION ALL SELECT f_id FROM t_table WHERE 1=1
AND f_data_source_id IN
#{dsId}
AND f_delete_flag != 1
AND f_update_time >= #{updateTime}
AND f_name LIKE CONCAT('%', #{keyword}, '%')
) AS tmp
